import PropTypes from 'prop-types';
import React, {Component} from 'react';
import {View, Text, TouchableOpacity} from "react-native";
import register from "../clickFunctions/register";
import * as Font from "expo-font";

export default class MyButton extends Component {
    constructor() {
        super();
        this.state = {fontLoaded: false}
    }

    componentDidMount = async () => {
        await Font.loadAsync({
            'myFont': require('../myfont.ttf'), // Uwaga: proszę w nazwie fonta nie używać dużych liter
        });
        this.setState({fontLoaded: true})
    }
    click = event => {
        if (this.props.action === 'register')
            register(this.props.values, this.props.navigation)
        else if (this.props.action === 'delete') {
            fetch("http://localhost:3000/delete?id=" + this.props.userId,)
                .then(response => response.json())
                .then(data => this.props.navigation.navigate('Users', {users: data.users}))
        } else if (this.props.action === 'details')
            this.props.navigation.navigate('UserDetails', {
                username: this.props.username,
                password: this.props.password,
                date: this.props.date
            })

    }

    render() {
        const style = {
            border: '2px solid black',
            textAlign: 'center',
        }
        return (
            <TouchableOpacity style={this.style} onPress={this.click}>
                <Text style={{fontWeight: 'bold', fontSize: 60}}> {this.props.title} </Text>
            </TouchableOpacity>
        )
    }
}

MyButton.propTypes = {
    title: PropTypes.string.isRequired,
    action: PropTypes.string.isRequired,
    navigation: PropTypes.object.isRequired
};
