import React, {Component} from 'react';
import {
    View,
    KeyboardAvoidingView,
    TextInput,
    StyleSheet,
    Text,
    Platform,
    TouchableWithoutFeedback,
    Button,
    Keyboard,
    SafeAreaView,
    FlatList,
    Switch
} from 'react-native';
import MyButton from "./MyButton";
import Location from './Location'
import * as Permissions from "expo-permissions";
import * as expoLocation from "expo-location";
import {AsyncStorage} from "react-native"

export default class extends Component {
    constructor() {
        super();
        this.state = {data: []}
    }

    setPermissions = async () => {
        let {status} = await Permissions.askAsync(Permissions.LOCATION);
        if (status !== 'granted') {
            alert('Odmawiam przydzielenia uprawnień do czytania lokalizacji')
        }
    }

    readData = async () => {
        let data = await AsyncStorage.getItem('locations');
        data = JSON.parse(data)
        if (data)
            this.setState({data: data})
    }

    updateState = (element) => {
        this.setState({data: element})
    }

    componentDidMount() {
        this.setPermissions()
        this.readData()
    }


    render() {
        const renderItem = ({item}) => (<Location timeStamp={item.timestamp}/>)
        return [
            <View style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}><MyButton
                title={'Pobierz i zapisz pozycję'}
                action={'save'}
                navigation={this.props.navigation}
                update = {this.updateState}
            /><MyButton
                title={'Usuń wszystkie dane'} action={'delete_all'} navigation={this.props.navigation}/>
                <MyButton
                    title={'Przejdź do mapy'} action={'map'} navigation={this.props.navigation}
                /></View>,
            <View>
                <Switch/>
            </View>,
            <SafeAreaView style={{flex: 1}}>
                <FlatList
                    data={this.state.data}
                    renderItem={renderItem}
                    keyExtractor={item => item.timestamp.toString()}
                />
            </SafeAreaView>
        ]
            ;
    }
}
